INSERT INTO `data` VALUES (779, 'http://shoaresal.ir/fa/tehran', ' www.tehranruydad.ir', NULL, ' طلب‌ قانونی شهرداری تهران از دولت فهرست شد ', NULL, 'درحالی‌که برخی از طلب‌ شهرداری‌های کشور از دولت به‌صورت تهاتر پرداخت شده، از طلب‌های تهران فهرستی تهیه شده است.با توجه به پیش‌بینی‌های قانونی در بودجه‌های سال 97و 98کل کشور، تاکنون شهرداری‌های تهران و مشهد توانسته‌اند بخشی از بدهی‌های خود به بانک‌ها را از محل بدهی بانک‌ها به دولت تهاتر کنند. در عین حال، جریمه‌های رانندگی، عوارض موضوع قانون مالیات بر ارزش افزوده، کمک 50درصدی به توسعه حمل‌ونقل ریلی و یارانه بلیت بخشی از مواردی است که در قوانین موضوعه یا قوانین بودجه سنواتی به‌عنوان تکالیف دولت در قبال شهرداری‌ها برشمرده شده و شهرداری تهران در اقدامی ابتکاری تمامی این موارد را فهرست و به دولت ارائه کرده است.', NULL, NULL, NULL, NULL, NULL, '<div class=\"fn20 article-body\">                       <p style=\"text-align: center;\"></p>     <script> 	var galleryMakerNWNmMmYNjBiYWZmMw = new GalleryMaker( 		15820, 		', 'a:19:{i:0;s:10:\"تهران\";i:1;s:18:\"شعار سال 98\";i:2;s:27:\"روزنامه همشهری\";i:3;s:7:\"AUHVSHG\";i:4;s:24:\"شعار سال جدید\";i:5;s:26:\"سال رونق تولید\";i:6;s:32:\"نامگذاری سال جدید\";i:7;s:26:\"نامگذاری سال 98\";i:8;s:27:\"نامگذاری امسال\";i:9;s:52:\"نامگذاری اقتصادی در سال جدید\";i:10;s:43:\"شهر هوشمند در کلانشهرها\";i:11;s:56:\"معاون برنامه ریزی شهردار تهران\";i:12;s:48:\"رئیس سزامان برنامه و بودجه\";i:13;s:48:\"بدهی دولت به شهرداری تهران\";i:14;s:58:\"مطالبات شهرداری از دولت در سال 98\";i:15;s:34:\"معاون شهردار تهران\";i:16;s:56:\"برنامه دوم توسعه شهرداری تهران\";i:17;s:61:\"اعتبارات برای توسعه شهری در تهران\";i:18;s:21:\"پیروز حناچی\";}', '/files/fa/news/1398/2/1/505577_250.jpg', '2019-04-22 18:08:26', NULL, NULL);
