INSERT INTO `data` VALUES (814, 'http://shoaresal.ir/fa/kordestan', ' www.showan.ir', NULL, ' انجام تمهیدات لازم برای مقابله باسیل ', NULL, 'شهردار سنندج گفت: با توجه به هشدارهای ستاد بحران استان کردستان در چند روز گذشته در راستای امکان وقوع سیلاب در سطح شهر سنندج، تمهیدات لازم برای مقابله با این بحران اندیشیده شده است.شهردار سنندج ادامه داد: در حال حاضر مهمترین دغدغه ما ناحیه‌های منفصل شهری و شهرک‌های اغماری هستند که بدون هیچ‌گونه مجوز ساخت و سازی در چند سال گذشته در بستر رودخانه‌ها و حتی رودخانه‌های خشک شده سال‌های گذشته، ساخت و ساز در آنها صورت گرفته و افراد بسیاری در این مناطق سکونت دارند.', NULL, NULL, NULL, NULL, NULL, '<div class=\"fn20 article-body\">                       <p style=\"text-align: center;\"></p>     <script> 	var galleryMakerNWNmMmYNjBiYWZmMw = new GalleryMaker( 		15820, 		', 'a:23:{i:0;s:14:\"کردستان\";i:1;s:18:\"شعار سال 98\";i:2;s:23:\"خبرگزاری مهر\";i:3;s:7:\"AUHVSHG\";i:4;s:24:\"شعار سال جدید\";i:5;s:26:\"سال رونق تولید\";i:6;s:32:\"نامگذاری سال جدید\";i:7;s:26:\"نامگذاری سال 98\";i:8;s:23:\"نامگذاری سال\";i:9;s:27:\"نامگذاری امسال\";i:10;s:52:\"نامگذاری اقتصادی در سال جدید\";i:11;s:26:\"حشمت الله صیدی\";i:12;s:28:\"شورای شهر سنندج\";i:13;s:42:\"خطر وقوع سیل در کردستان\";i:14;s:6:\"سیل\";i:15;s:10:\"سیلاب\";i:16;s:30:\"سیلاب و آبگرفتگی\";i:17;s:26:\"سیل و آبگرفتگی\";i:18;s:26:\"سیلاب های فصلی\";i:19;s:24:\"سیل در گلستان\";i:20;s:22:\"سیل در شیراز\";i:21;s:53:\"مقابله با وقوع سیل در کردستان\";i:22;s:73:\"آماده باش خدمات شهری سنندج برای وقوع سیل\";}', '/files/fa/news/1398/1/11/492111_506.jpg', '2019-04-22 18:15:53', NULL, NULL);
