INSERT INTO `data` VALUES (819, 'http://shoaresal.ir/fa/kermanshah', ' www.Dayikan.ir', NULL, ' تعطیلی ادارات در مسیر سیل ', NULL, 'استاندار کرمانشاه از تعطیلی ادارات در معرض سیل کرمانشاه خبر داد و گفت: در صورت نیاز بازارچه‌های مرزی نیز تعطیل خواهد شد.وی بر لزوم اطلاع‌رسانی درست به مردم هم تاکید کرد و ادامه داد: مردم باید بدانند سیل با کسی شوخی ندارد و از نزدیک شدن به سیلاب و عکس گرفتن با آن خودداری کنند و به هیچ عنوان تصور نکنند که امکان گذر از سیل را دارند. بازوند با اشاره به تدابیری که برای نقاط آسیب پذیر کرمانشاه اندیشیده شده، عنوان کرد: برای نواحی مختلف مانند ده‌پهن، تنگ کنشت و … با همکاری بخش‌های مختلف اقدامات خوبی برای خروج اب انجام شده است.', NULL, NULL, NULL, NULL, NULL, '<div class=\"fn20 article-body\">                       <p style=\"text-align: center;\"></p>     <script> 	var galleryMakerNWNmMmYNjBiYWZmMw = new GalleryMaker( 		15820, 		', 'a:25:{i:0;s:16:\"کرمانشاه\";i:1;s:18:\"شعار سال 98\";i:2;s:46:\"خبرگزاری دانشجویان ایران\";i:3;s:10:\"ایسنا\";i:4;s:7:\"AUHVSHG\";i:5;s:33:\"استاندار کرمانشاه\";i:6;s:23:\"هوشنگ بازوند\";i:7;s:24:\"شعار سال جدید\";i:8;s:26:\"سال رونق تولید\";i:9;s:32:\"نامگذاری سال جدید\";i:10;s:26:\"نامگذاری سال 98\";i:11;s:23:\"نامگذاری سال\";i:12;s:27:\"نامگذاری امسال\";i:13;s:52:\"نامگذاری اقتصادی در سال جدید\";i:14;s:28:\"سیل در کرمانشاه\";i:15;s:51:\"خسارت سیل در مناطق غربی کشور\";i:16;s:6:\"سیل\";i:17;s:10:\"سیلاب\";i:18;s:30:\"سیلاب و آبگرفتگی\";i:19;s:17:\"سیل زدگان\";i:20;s:26:\"سیلاب های فصلی\";i:21;s:24:\"سیل در گلستان\";i:22;s:22:\"سیل در شیراز\";i:23;s:29:\"رودخانه آبشوران\";i:24;s:73:\"نقاط آسیب پذیر برای وقوع سیل در کرمانشاه\";}', '/files/fa/news/1398/1/12/492634_612.png', '2019-04-22 18:16:51', NULL, NULL);
